import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const GraphContainer = styled(motion.div)`
  margin: 1.5rem 0;
  background: white;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(0, 0, 0, 0.05);
`;

const GraphHeader = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 1rem 1.5rem;
  font-weight: 600;
  display: flex;
  align-items: center;

  &::before {
    content: "ðŸ“Š";
    margin-right: 0.75rem;
    font-size: 1.1rem;
  }
`;

const GraphFrame = styled.iframe`
  width: 100%;
  height: 400px;
  border: none;
  display: block;
`;

const ErrorMessage = styled.div`
  padding: 2rem;
  text-align: center;
  color: #6b7280;
  font-style: italic;
`;

interface DesmosGraphProps {
  url: string;
}

export const DesmosGraph: React.FC<DesmosGraphProps> = ({ url }) => {
  if (!url) {
    return (
      <GraphContainer
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5 }}
      >
        <GraphHeader>Interactive Graph</GraphHeader>
        <ErrorMessage>
          Graph visualization not available for this problem.
        </ErrorMessage>
      </GraphContainer>
    );
  }

  return (
    <GraphContainer
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.5 }}
    >
      <GraphHeader>Interactive Graph</GraphHeader>
      <GraphFrame
        src={url}
        title="Interactive Mathematical Graph"
        loading="lazy"
      />
    </GraphContainer>
  );
};
