import React, { useState, useRef, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { ChatMessage, MathSolution, APIResponse } from '../types/mathviz';
import { MessageBubble } from './MessageBubble';
import { SolutionDisplay } from './SolutionDisplay';
import { DesmosGraph } from './DesmosGraph';
import { mathvizAPI } from '../services/api';

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
`;

const Header = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  padding: 2rem;
  margin: 2rem 2rem 1rem 2rem;
  text-align: center;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  animation: ${fadeInUp} 0.8s ease-out;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: 700;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  color: #6b7280;
  font-weight: 400;
  margin: 0;
`;

const MessagesContainer = styled.div`
  flex: 1;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  margin: 0 2rem;
  padding: 2rem;
  overflow-y: auto;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);

  /* Custom scrollbar */
  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.05);
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: rgba(0, 0, 0, 0.3);
  }
`;

const InputArea = styled.div`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  margin: 1rem 2rem 2rem 2rem;
  padding: 2rem;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
`;

const SuggestionsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 0.75rem;
  margin-bottom: 1.5rem;
`;

const SuggestionChip = styled(motion.button)`
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.1), rgba(118, 75, 162, 0.1));
  border: 1px solid rgba(102, 126, 234, 0.2);
  color: #4f46e5;
  padding: 0.75rem 1rem;
  border-radius: 12px;
  font-size: 0.85rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.15), rgba(118, 75, 162, 0.15));
    border-color: rgba(102, 126, 234, 0.3);
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(102, 126, 234, 0.2);
  }
`;

const InputRow = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const MathInput = styled.input`
  flex: 1;
  padding: 1rem 1.5rem;
  border: 2px solid rgba(102, 126, 234, 0.2);
  border-radius: 12px;
  font-size: 1rem;
  font-family: 'JetBrains Mono', monospace;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: rgba(102, 126, 234, 0.5);
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &::placeholder {
    color: #9ca3af;
    font-style: italic;
  }
`;

const SendButton = styled(motion.button)<{ disabled: boolean }>`
  background: ${props => props.disabled 
    ? 'linear-gradient(135deg, #9ca3af 0%, #6b7280 100%)'
    : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
  };
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 12px;
  font-weight: 600;
  cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
  display: flex;
  align-items: center;
  gap: 0.5rem;

  &:hover {
    transform: ${props => props.disabled ? 'none' : 'translateY(-1px)'};
    box-shadow: ${props => props.disabled ? 'none' : '0 4px 16px rgba(102, 126, 234, 0.3)'};
  }
`;

const TypingIndicator = styled(motion.div)`
  display: flex;
  align-items: center;
  padding: 1rem 1.25rem;
  background: #f3f4f6;
  border-radius: 20px 20px 20px 8px;
  margin: 1rem 0;
  color: #6b7280;
  font-size: 0.9rem;
`;

const TypingDot = styled(motion.span)`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: #6b7280;
  margin: 0 2px;
`;

const suggestions = [
  "Solve: 2xÂ² - 8x + 6 = 0",
  "Find derivative of xÂ³ + 4xÂ² - 2x + 1",
  "Integrate: âˆ«(3xÂ² + 2x + 1) dx",
  "Factor: xÂ² - 5x + 6",
  "Graph: y = xÂ² - 4x + 3",
  "Find critical points of xÂ³ - 3xÂ² + 2"
];

export const MathVizChat: React.FC = () => {
  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      id: '1',
      type: 'bot',
      content: "ðŸ‘‹ Hello! I'm your AI-powered math tutor. I can solve equations, find derivatives, compute integrals, and create interactive graphs. What would you like to learn today?",
      timestamp: new Date(),
      has_solution: false
    }
  ]);

  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (text: string) => {
    if (!text.trim() || isLoading) return;

    // Add user message
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: text,
      timestamp: new Date(),
      has_solution: false
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      // Call MathViz API
      const response = await mathvizAPI.chat(text);

      const botMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'bot',
        content: response.reply_text || `I'll solve that now: **${text}**`,
        timestamp: new Date(),
        solution: response.solution,
        desmos_url: response.desmos_url,
        desmos_config: response.desmos_config,
        has_solution: !!response.solution
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        type: 'bot',
        content: `ðŸ¤” I encountered an issue solving that problem: **${error instanceof Error ? error.message : 'Unknown error'}**\n\nCould you try rephrasing it or asking a different question? I'm here to help!`,
        timestamp: new Date(),
        has_solution: false
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSuggestionClick = (suggestion: string) => {
    handleSendMessage(suggestion);
  };

  const handleInputSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    handleSendMessage(inputValue);
  };

  const showSuggestions = messages.length <= 1;

  return (
    <ChatContainer>
      <Header
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <Title>ðŸ§® MathViz</Title>
        <Subtitle>Your AI-powered mathematical companion with step-by-step solutions</Subtitle>
      </Header>

      <MessagesContainer>
        <AnimatePresence>
          {messages.map((message) => (
            <motion.div
              key={message.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              <MessageBubble message={message} />
              {message.solution && (
                <SolutionDisplay solution={message.solution} />
              )}
              {(message.desmos_url || message.desmos_config) && (
                <DesmosGraph 
                  url={message.desmos_url}
                  expressions={message.desmos_config?.expressions}
                  viewport={{
                    xmin: message.desmos_config?.xmin,
                    xmax: message.desmos_config?.xmax,
                    ymin: message.desmos_config?.ymin,
                    ymax: message.desmos_config?.ymax,
                  }}
                />
              )}
            </motion.div>
          ))}
        </AnimatePresence>

        {isLoading && (
          <TypingIndicator
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
          >
            <span>MathViz is solving your problem</span>
            {[0, 1, 2].map(i => (
              <TypingDot
                key={i}
                animate={{ 
                  scale: [0.8, 1.2, 0.8],
                  opacity: [0.5, 1, 0.5]
                }}
                transition={{
                  duration: 1.4,
                  repeat: Infinity,
                  delay: i * 0.2
                }}
              />
            ))}
          </TypingIndicator>
        )}

        <div ref={messagesEndRef} />
      </MessagesContainer>

      <InputArea>
        {showSuggestions && (
          <div>
            <h3 style={{ marginBottom: '1rem', color: '#374151', fontWeight: 600 }}>
              ðŸ’¡ Try these examples:
            </h3>
            <SuggestionsGrid>
              {suggestions.map((suggestion, index) => (
                <SuggestionChip
                  key={index}
                  onClick={() => handleSuggestionClick(suggestion)}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  {suggestion}
                </SuggestionChip>
              ))}
            </SuggestionsGrid>
          </div>
        )}

        <form onSubmit={handleInputSubmit}>
          <InputRow>
            <MathInput
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              placeholder="Type your math problem here... (e.g., 'Solve: 2xÂ² - 8x + 6 = 0')"
              disabled={isLoading}
            />
            <SendButton
              type="submit"
              disabled={isLoading || !inputValue.trim()}
              whileHover={{ scale: isLoading ? 1 : 1.05 }}
              whileTap={{ scale: isLoading ? 1 : 0.95 }}
            >
              {isLoading ? 'ðŸ”„' : 'âœ¨'} Send
            </SendButton>
          </InputRow>
        </form>
      </InputArea>
    </ChatContainer>
  );
};
